import React,{useState} from 'react'
import {BrowserRouter as Router,Switch,Route} from 'react-router-dom';

import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import "./css/app.css";

import SignUp from "./components/SignUp";
import Footer from "./components/Footer"
import LoginPage from "./components/LoginPage"
import Head from "./components/Head";
import Dashboard from './components/Dashboard';

function App()
{
  const [isLoggedIn,setIsLoggedIn] = useState(true);
  const [email,setEmail] = useState("");
  const [password,setPassword] = useState("");
  const [id,setId] = useState("");
  // const [currentPage,setCurrentPage] = useState(<LoginPage onSubmit={(e)=>navigate(e)} />)

  let navigate=(data)=>{

    alert(data.email+data.password+data.isLoggedIn);
    setEmail(data.email);
    setPassword(data.password);
    setIsLoggedIn(data.isLoggedIn);
    setId(data.id);

    // if(isLoggedIn)
    // {
    //   setCurrentPage();
    // }
    // else{
    //   setCurrentPage();
    // }
  }
  return (
    <div>
      <Router> 

        <Head loggedIn={isLoggedIn}/>
        {isLoggedIn?<Dashboard userId={id} />:<LoginPage onSubmit={(e)=>navigate(e)} />}
        <Footer />

        <Switch>
          {/* <Route path="/" exact>{currentPage}</Route> */}
          {/* <Route path="/dash" component={Dashboard}> <Dashboard /> </Route> */}
           <Route path="/signup" exact component={SignUp}> <SignUp /> </Route>
        </Switch>

      </Router>
    </div>
  );
}

export default App;
