import React,{Component} from 'react'
import {BrowserRouter as Router,Switch,Route} from 'react-router-dom';
import SignUp from "./components/SignUp";
import Footer from "./components/Footer"
import LoginPage from "./components/LoginPage"
import Head from "./components/Head";

import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import "./css/app.css";
import { createBrowserHistory } from "history";
import Dashboard from './components/Dashboard';

class App extends Component
{
  // const history = createBrowserHistory();
  // const[isLoggedIn,setIsLoggedIn] = useState(false);
  // const[email,setEmail] = useState("");
  // const[password,setPassword] = useState("");

  constructor(props)
  {
    super(props);
    this.state={
      email:"",
      password:"",
      isLoggedIn:false
    }
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange=(data)=>
  {
    console.log(data);
    this.state.email=data.email;
    this.state.password =data.password;
    this.state.isLoggedIn =data.status;
  }
  render()
  {
  return (
    <div>
      {/* // history={history} */}
      <Router> 
        <Head logStatus={this.state.isLoggedIn}/>
            <div>
              <Switch>
                  <Route path="/" exact component={LoginPage}><LoginPage onChange={(event) => this.handleChange(event)}/></Route>
                  <Route path="/dash" ><Dashboard /></Route>
                  <Route path="/signup" exact><SignUp /></Route>
                  <Route path="/signin" exact><LoginPage /></Route>
              </Switch>
            </div>
        <Footer />
      </Router>
      </div>
  );
}
}

export default App;
