import React, { Component } from "react";
import "../css/login.css";
import UserService from "../services/UserService";
import {Redirect} from 'react-router-dom';
import axios from '../../node_modules/axios';

class LoginPage extends Component {

    constructor(props){
        super(props);
        this.state={
            email:"",
            password:""
        }

        this.myChangeHandler=this.myChangeHandler.bind(this);
        // this.mySubmitHandler=this.mySubmitHandler.bind(this);
     }
    myChangeHandler(event)
    {
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]:val})
    }
    // mySubmitHandler(event)
    // {
    //     if(this.state.email.length>0 && this.state.password.length>0)
    //     {
    //         let user =  UserService.getUserByEmail(this.state.email);

    //         if(!user){
    //             alert("please sign up");
    //         }
    //         else{
    //             alert("welcome ");
    //             <Redirect to="/dashboard"/>
    //         }
    //     }
    //     else
    //     {
    //         alert("enter email and password to login!");
    //     }

    // }

    handleSubmit = async event => {
        event.preventDefault();
        const USER_API_BASE_URL = "http://localhost:8084/api/v1/users";

        const response = await axios.get(USER_API_BASE_URL + '/' + this.state.email);

        console.log(response);
        console.log(response.data);
      };

    render() {
        return (
            <div class="outer">
            <form onSubmit={this.handleSubmit}>
                <div className="Model">
                    <h2 className="tag">Login</h2>

                    <input name="email" onChange={this.myChangeHandler} value={this.state.email} type="email" placeholder="Email" />

                    <input name="password" onChange={this.myChangeHandler} value={this.state.password} type="password" placeholder="Password" />

                    <input type="submit" value="submit" className="bt" />

                    <p className="forgot-password text-right">
                        Forgot <a href="#">password?</a>
                    </p>
                </div>
            </form>
            </div>
        );
    }
}
export default LoginPage;
