import React, { Component } from "react";
import "../css/login.css"
import UserService from "../services/UserService"

export default class SignUp extends Component {

    constructor(props){
        super(props);
        this.state={
            username:"",
            password:"",
            firstname:"",
            lastname:""

        }
    
        this.myChangeHandler=this.myChangeHandler.bind(this);
        this.mySubmitHandler=this.mySubmitHandler.bind(this);
     }
    myChangeHandler(event)
    {
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]:val})
    }
    mySubmitHandler(event)
    {
        if(this.state.username.length>0 && this.state.password.length>0 && this.state.firstname.length>0 && this.state.lastname.length>0)
        {
            let user = {firstname:this.state.firstname,lastname:this.state.lastname,username:this.state.username,password:this.state.password};
            UserService.createUser(user)
            alert("Registrai")
        }
        
    }

    render() {
        return (
            <div class="outer">
            <form onSubmit={this.mySubmitHandler}>
                <div className="Model">
                    <h2 className="tag">Register</h2>
                    
                    <input type="text" name="firstname" onChange={this.myChangeHandler} value={this.state.firstname} placeholder="First name" />
                    
                    <input type="text" name="lastname" onChange={this.myChangeHandler} value={this.state.lastname} placeholder="Last name" />
            
                    <input name="email" onChange={this.myChangeHandler} value={this.state.username} type="email" placeholder="Email" />
        
                    <input name="password" onChange={this.myChangeHandler} value={this.state.password} type="password" placeholder="Password" />
    
                    <input type="submit" value="Submit" className="bt" />
    
                    <p className="forgot-password">
                        Already registered <a href="#">log in?</a>
                    </p>
                </div>
            </form>
            </div>
            
        );
    }
}
