import react,{Component} from 'react';
import reactDom from 'react-dom';
import '../css/dash.css';
import $ from '../../node_modules/jquery';
import "../../node_modules/font-awesome/css/font-awesome.min.css";
import "../../node_modules/bootstrap/dist/css/bootstrap.min.css";
import Card from "./Card.js";
import hin from "../images/hindu.jpg";
import toi from "../images/toi.png";
import pat from "../images/patrika2.jpg";

class Dashboard extends Component {

  constructor(props)
  {
    super(props);
  }
  componentDidMount()
  {
    $(".sidebar-dropdown > a").click(function() {
      $(".sidebar-submenu").slideUp(200);
      if (
        $(this)
          .parent()
          .hasClass("active")
      ) {
        $(".sidebar-dropdown").removeClass("active");
        $(this)
          .parent()
          .removeClass("active");
      } else {
        $(".sidebar-dropdown").removeClass("active");
        $(this)
          .next(".sidebar-submenu")
          .slideDown(200);
        $(this)
          .parent()
          .addClass("active");
      }
    });

    $("#close-sidebar").click(function() {
      $(".page-wrapper").removeClass("toggled");
    });
    $("#show-sidebar").click(function() {
      $(".page-wrapper").addClass("toggled");
    });

  }

  render()
  {
  return (
    <div class="page-wrapper chiller-theme">

        <a id="show-sidebar" class="btn btn-sm btn-dark">
          <i class="fa fa-bars"></i>
        </a>

        <nav id="sidebar" class="sidebar-wrapper">

          <div class="sidebar-content">

            <div class="sidebar-brand">
              <a href="#"></a>

              <div id="close-sidebar">
                <i class="fa fa-times"></i>
              </div>
            </div>

            <div class="sidebar-header">
              <div class="user-pic">
                <img class="img-responsive img-rounded" src="https://raw.githubusercontent.com/azouaoui-med/pro-sidebar-template/gh-pages/src/img/user.jpg" alt="User picture"/>
              </div>
              <div class="user-info">
                <span class="user-name">Hritik
                  <strong> Arora</strong>
                </span>
                <span class="user-status">
                  <i class="fa fa-circle"></i>
                  <span>Online</span>
                </span>
              </div>
            </div>

            {/* <!-- sidebar-header  --> */}

            <div class="sidebar-search">
              <div>
                <div class="input-group">
                  <input type="text" class="form-control search-menu" placeholder="Search..."/>
                  <div class="input-group-append">
                    <span class="input-group-text">
                      <i class="fa fa-search" aria-hidden="true"></i>
                    </span>
                  </div>
                </div>
              </div>
            </div>

      {/* <!-- sidebar-search  --> */}

              <div class="sidebar-menu">
                <ul>
                  <li class="header-menu">
                    <span>General</span>
                  </li>

                  <li class="sidebar-dropdown">
                    <a href="#">
                      <i class="fa fa-tachometer-alt"></i>
                      <span>Dashboard</span>
                    </a>
                  </li>
                  <li class="sidebar-dropdown">
                    <a href="#">
                      <i class="fa fa-ad"></i>
                      <span>Post an Ad</span>
                    </a>
                  </li>

                </ul>
              </div>
      {/* <!-- sidebar-menu  --> */}
          </div>
    {/* <!-- sidebar-content  --> */}

  </nav>
  {/* <!-- sidebar-wrapper  --> */}

  <main class="page-content">

    <div class="container">

      <div class="row">
        <Card src={toi} alt={"times of india"} title={"TOI"}/>
        <Card src={pat} alt={"patrika"} title={"Patrika"}/>
        <Card src={hin} alt={"hindu"} title={"The Hindu"}/>
      </div>
      </div>
  </main>
 </div> 
  );
 }
}

export default Dashboard;
