import React,{Component,useState} from 'react'
import {BrowserRouter as Router,Switch,Route} from 'react-router-dom';
import SignUp from "./components/SignUp";
import Footer from "./components/Footer"
import LoginPage from "./components/LoginPage"
import Head from "./components/Head";

import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import "./css/app.css";
import Dashboard from './components/Dashboard';

function App()
{
  // const history = createBrowserHistory();
  const [isLoggedIn,setIsLoggedIn] = useState(true);
  const [email,setEmail] = useState("");
  const [password,setPassword] = useState("");

  let navigate=(data)=>{
    alert(data.email+data.password+data.isLoggedIn);
    setEmail(data.email);
    setPassword(data.password);
    setIsLoggedIn(data.isLoggedIn);
    alert("welcome "+email);
  }

  return (
    <div>
      <Router> 
        <Head />
            <div>
              <Switch>
                  <Route path="/" exact Component={Dashboard}>{isLoggedIn?<Dashboard />:<LoginPage onSubmit={(e)=>navigate(e)}/>}</Route>
                  <Route path="/dash" component={Dashboard}><Dashboard /></Route>
                  <Route path="/signup" exact component={SignUp}><SignUp /></Route>
                  {/* <Route path="/signin" exact component={LoginPage}><LoginPage onSubmit={} /></Route> */}
              </Switch>
            </div>
        <Footer />
      </Router>
      </div>
  );
}


export default App;
