import React,{Component,useState} from 'react'
import {BrowserRouter as Router,Switch,Route} from 'react-router-dom';
import SignUp from "./components/SignUp";
import Footer from "./components/Footer"
import LoginPage from "./components/LoginPage"
import Head from "./components/Head";

import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import "./css/app.css";
import { createBrowserHistory } from "history";
import Dashboard from './components/Dashboard';

function App()
{
  // const history = createBrowserHistory();
  const[isLoggedIn,setIsLoggedIn] = useState(false);
  const[email,setEmail] = useState("");
  const[password,setPassword] = useState("");

  let navigate=(data)=>{
    setEmail(data.email);
    setPassword(data.password);
    setIsLoggedIn(data.isLoggedIn);
  }

  return (
    <div>
      {/* // history={history} */}
      <Router> 
        <Head />
            <div>
              <Switch>
                  <Route path="/" exact component={LoginPage}>{isLoggedIn?<Dashboard />:<LoginPage onSubmit={navigate()}/>}</Route>
                  {/* <Route path="/dash" component={Dashboard}><Dashboard /></Route> */}
                  <Route path="/signup" exact component={SignUp}><SignUp /></Route>
                  <Route path="/signin" exact component={LoginPage}><LoginPage /></Route>
              </Switch>
            </div>
        <Footer />
      </Router>
      </div>
  );
}


export default App;
