import React, { Component } from "react";
import "../css/login.css";
import {Redirect} from 'react-router-dom';
import axios from '../../node_modules/axios';
import { useHistory } from "react-router-dom";
import App from "../App";
class LoginPage extends Component {

    constructor(props){
        super(props);
        this.state={
            email:"",
            password:""
        }
        // const history = useHistory();
        this.myChangeHandler=this.myChangeHandler.bind(this);
    }
    myChangeHandler(event)
    {
        let nam = event.target.name;
        let val = event.target.value;
        this.setState({[nam]:val})
    }

    handleSubmit = async event => {
        event.preventDefault();
        const USER_API_BASE_URL = "http://localhost:8084/api/v1/users";

        const response = await axios.get(USER_API_BASE_URL + '/' + this.state.email);

        console.log(response);
        console.log(response.data);
        let res = response.data;
        if(res.password == this.state.password)
        {
            alert("login success!");
            return <Redirect to="/">{App}</Redirect>
        }
        else{
            alert("Incorrect credentials!")
        }
      };

    render() {
        return (
            <div class="outer">
            <form onSubmit={this.handleSubmit}>
                <div className="Model">
                    <h2 className="tag">Login</h2>

                    <input name="email" onChange={this.myChangeHandler} value={this.state.email} type="email" placeholder="Email" />

                    <input name="password" onChange={this.myChangeHandler} value={this.state.password} type="password" placeholder="Password" />

                    <input type="submit" value="submit" className="bt" />

                    <p className="forgot-password text-right">
                        Forgot <a href="#">password?</a>
                    </p>
                </div>
            </form>
            </div>
        );
    }
}
export default LoginPage;
