import React, {useState} from "react";
import "../css/login.css";
import { useHistory} from "react-router-dom";
import UserService, { USER_NAME_SESSION_ATTRIBUTE_NAME } from "../services/UserService";
import {Nav,NavDropdown,Navbar,Form,FormControl, NavLink} from 'react-bootstrap';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
function LoginPage(props){

    const [email,setEmail] = useState("");
    const [password,setPassword] = useState("");    

    const history = useHistory();

    const handleSubmit = () => {

        if(UserService.validateUser(email,password))
        {
            alert("login success !");
            history.push("/dashboard");
        }
        else{
            alert("incorrect credentials")
        }
      };

    const [modal, setModal] = useState(false);

    const toggle = () => setModal(!modal);

    return (

        <div className="page-wrapper">
            <section className="hero-section">
                <Navbar  style={{display:"flex",justifyContent:"right",backgroundColor: "rgb(50, 50, 50)",zIndex: "100",height:"76px"}} expand="lg" >
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Form inline style={{margin:"10px"}}>
                            <FormControl style={{width:"300px"}} type="text" placeholder="Search" className="mr-sm-2" />
                            <i style={{color:"white"}} className="fa fa-search"></i>
                        </Form>
                        <Button color="danger" onClick={toggle}>login</Button>
                    </Navbar.Collapse>  
                </Navbar>

                <Modal isOpen={modal} toggle={toggle} className={className}>
                    <ModalBody>
                        <form onSubmit={handleSubmit} >
                            <div className="Model-in">
                                <h2 className="tag">Log In!</h2>

                                <input name="email" type="email" value={email} onChange={(Event)=>{setEmail(Event.target.value)}} placeholder="Email" /><br/>

                                <input name="password" type="password" value={password} onChange={(Event)=>{setPassword(Event.target.value)}} placeholder="Password" /><br/>

                                <input type="submit" value="submit" className="bt" />

                                <p style={{color:"whitesmoke"}} className="forgot-password">
                                    Forgot <a href="#">password?</a>
                                </p>
                            </div>
                        </form>
                    </ModalBody>
                </Modal>
            </section>
            <section className="about-section">

            </section>
        </div>
    //     <div className="outer">

    //    </div>
    );
}
export default LoginPage;
