import react,{Component} from 'react';
import reactDom from 'react-dom';
import '../css/dash.css';
import $ from '../../node_modules/jquery';
import "../../node_modules/font-awesome/css/font-awesome.min.css";
import "../../node_modules/bootstrap/dist/css/bootstrap.min.css";
import Card from "./Card.js";
import hin from "../images/hindu.jpg";
import toi from "../images/toi.png";
import pat from "../images/patrika2.jpg";
import {Redirect} from 'react-router-dom';
import PostAd from './PostAd';

class Dashboard extends Component {

  constructor(props)
  {
    super(props);
    this.state={
      currentPage:"Dashboard",
      companyName:""

    }

  }

  componentDidMount()
  {
    $(".sidebar-dropdown > a").click(function() {
      // $(".sidebar-submenu").slideUp(200);
      if($(this).parent().hasClass("dash"))
      {
        this.setState({currentPage:"Dashboard"}).bind(this);
      }
      else
      {
        this.setState({currentPage:"Ad"}).bind(this);
      }
      if ( $(this).parent().hasClass("active") ) 
      {
        $(".sidebar-dropdown").removeClass("active");
        $(this)
          .parent()
          .removeClass("active");
      }
      else 
      {
        $(".sidebar-dropdown").removeClass("active");
        // $(this)
        //   .next(".sidebar-submenu")
        //   .slideDown(200);
        $(this)
          .parent()
          .addClass("active");
      }
    });

    $("#close-sidebar").click(function() {
      $(".page-wrapper").removeClass("toggled");
    });
    $("#show-sidebar").click(function() {
      $(".page-wrapper").addClass("toggled");
    });

  }

  render()
  {
    this.myChangeHandler=(event)=>
    {
      let val = event.target.value;
        this.setState({companyName:val});
    }
  return (
    <div>
    <div class="sidebar">
      <a href class="active" onClick={this.setState({currentPage:"Dashboard"})}>Home</a>
      <a href="/" onClick={this.setState({currentPage:"Ad"})} >Post an Ad</a>
      <a href="#contact">Contact</a>
      <a href="#about">About</a>
    </div>

    <div class="content">
      <div>
      {
        this.state.currentPage==="Dashboard"?

        <div class="row">
          <Card src={toi} alt={"times of india"} title={"TOI"} onClick={(e)=>this.handleClick(e)} />
          <Card src={pat} alt={"patrika"} title={"Patrika"} onClick={(e)=>this.handleClick(e)} />
          <Card src={hin} alt={"hindu"} title={"The Hindu"} onClick={(e)=>this.handleClick(e)} />
        </div>
        :
          <div className="container">
              <div className="row">
                  <div className="col-md-8">
                      <form className="form-group">
                          <h2 className="tag">Post an Advertisement!</h2><br/>

                          <select name="companyName" onChange={this.myChangeHandler} value={this.state.companyName} > 
                              <option value= "company name">Select Company</option>
                          </select><br/>
                          <hr/>
                          <label for="Ad_post_date">Select Date : </label>
                          <input type="date" name="Ad_post_date" placeholder="Ad post date" /><br/>
                          <hr/>
                          <textarea type="text" name="Description" placeholder="Description" /><br/>

                      </form>
                  </div>
              </div>
          </div>
      }
    </div>
    </div>
    </div>
  );
 }
}

export default Dashboard;
